mapper.update:
	
	UPDATE NOTICE
	SET NTITLE=#{nTitle}, NCONTENT=#{nContent}, FILEPATH=#{filePath}, CID=#{cId}, NOTICEYN=#{noticeYN}
 	WHERE NID=#{nId}

UPDATE NOTICE 
SET STATUS='N'
WHERE NID=#{nId}

UPDATE REPLY
SET RCONTENT=#{rContent}
WHERE RID=#{rId}

UPDATE REPLY
SET STATUS='N'
WHERE RID=#{rId}

mapper.select:
SELECT COUNT(*)
FROM NLIKE
WHERE USER_NO=#{userNo} AND NID=#{nId}

SELECT COUNT(*)
FROM NLIKE
WHERE NID=#{nId}

mapper.delete:
DELETE 
FROM NLIKE
WHERE USER_NO=#{userNo} AND NID=#{nId}
