mapper.select:
SELECT *
FROM MEMBER
WHERE USER_NO=#{userNo}

mapper.update:
UPDATE TEAM_AD
SET TEAM_ADVER=#{teamAdver}, RECRUITCOUNT=#{recruitCount}
WHERE TEAM_NO = #{teamNo}

mapper.select:
SELECT TEAM_NO,TEAM_NAME
FROM TEAM
WHERE USER_NO=#{userNo}

SELECT COUNT(*)
FROM TEAM
WHERE TEAM_NAME=#{teamName}

SELECT TEAM_NO
FROM TEAM
WHERE TEAM_NAME=#{createdTeamName}

SELECT COUNT(*)
FROM TEAM_AD
WHERE TEAM_NO = #{teamNo}

SELECT COUNT(*)
FROM T_JOINED_MEMBER
WHERE TEAM_NO=#{teamNo} AND USER_NO=#{userNo}

mapper.update:
UPDATE TEAM_AD
SET RECRUITCOUNT = RECRUITCOUNT -1
WHERE TEAM_NO = #{teamNo}

mapper.select:
SELECT *
FROM TEAM 
WHERE TEAM_NO = #{tId}

SELECT COUNT(*)
FROM T_MEMBER
WHERE TEAM_NO = #{tId}
